const config = {
    apiKey: "AIzaSyCgsDSaHKYWwjMonqXsJ9N-G1RvIoNQDpA",
    authDomain: "fitness-e3bc6.firebaseapp.com",
    databaseURL: "https://fitness-e3bc6-default-rtdb.europe-west1.firebasedatabase.app/",
    projectId: "fitness-e3bc6",
    storageBucket: "fitness-e3bc6.appspot.com",
    messagingSenderId: "382581057656",
    appId: "1:382581057656:web:14ea60b005cbaab294658d"
  };

class Firebase {
    constructor() {
        app.initializeApp(config);
        this.auth = app.auth();
        this.db = app.database();
    }

    /*** Authentication  ***/
    doCreateUserWithEmailAndPassword = (email, password) => 
        this.auth.createUserWithEmailAndPassword(email, password);

    doSignInWithEmailAndPassword = (email, password) => 
        this.auth.signInWithEmailAndPassword(email, password);

    doSignOut = () => 
        this.auth.signOut();

    doPasswordReset = email => 
        this.auth.sendPasswordResetEmail(email);

    /*** Database ***/
    user = uid => this.db.ref(`users/${uid}`);
    users = () => this.db.ref('users');

    addActivity = (uid, activity) => {
        const ref = this.db.ref().child(`users/${uid}/activities`);
        ref.push(activity);
    };

    updateActivity = (uid, activity, activityKey) => {
        const ref = this.db.ref().child(`users/${uid}/activities/${activityKey}`);
        ref.update(activity);
    }
}

export default Firebase;
